mapper.select:
SELECT * FROM (SELECT T.*,ROWNUM R FROM ( SELECT c.* FROM community c JOIN users u ON c.id=u.id
WHERE ${searchColumn} LIKE '%' || #{searchWord} || '%'
ORDER BY lno DESC) T) WHERE R BETWEEN #{start} AND #{end}

SELECT * FROM (SELECT T.*,ROWNUM R FROM ( SELECT cc.*,c.ctitle FROM commucomment cc JOIN community c ON cc.lno=c.lno
WHERE ${searchColumn} LIKE '%' || #{searchWord} || '%'
ORDER BY cno DESC) T) WHERE R BETWEEN #{start} AND #{end}
